import { FaTemperatureHigh, FaCloudRain, FaWind } from 'react-icons/fa';

function WeatherCityDisplay({ data }) {
    const maxPrecipitation = 10; // Maximum precipitation in mm for 100%
    const precipitation = data.rain ? data.rain["1h"] || data.rain["3h"] : data.snow ? data.snow["1h"] || data.snow["3h"] : 0;
    const precipitationPercentage = (precipitation / maxPrecipitation) * 100;

    return (
        <div className="container">
            <div className="top">
                {data.main && (
                    <div>
                        <h1><FaTemperatureHigh /> {data.main.temp.toFixed()}°F</h1>
                        <p>{data.weather ? data.weather[0].main : null} <FaCloudRain /></p>
                        <p>Precipitation: {Math.min(100, precipitationPercentage.toFixed(2))}%</p>
                        <p>Humidity: {data.main.humidity}%</p>
                        {data.wind && <p><FaWind /> Wind Speed: {data.wind.speed.toFixed()} MPH</p>}
                    </div>
                )}
            </div>
        </div>
    );
}

export default WeatherCityDisplay;
